function minWindow(s: string, t: string): string {
    if (s === t) { return s; }

    const tArr = t.split('').sort((a,b) => a.localeCompare(b));

    let low = t.length;
    let high = s.length;

    let window = high; // Math.floor((low + high) / 2);
    let windowStr = ''; // s.slice(low, low + window);

    do {
        console.log(low, high, window);
        let tooHigh = false;

        for (let i = 0; i <= s.length - window; i++) {
            windowStr = s.slice(i, i + window);

            if (isIncluded(windowStr, tArr)) {
                tooHigh = true;
                break;
            }
        }

        if (tooHigh) {
            high = window;
        } else {
            low = window;
        }

        window = Math.floor((low + high) / 2);
    } while (high > (low + 1));

    return windowStr;
};


function isIncluded(s: string, tArr: string[]): boolean {
    const sArr = s.split('').sort((a,b) => a.localeCompare(b));

    let sPos = 0, tPos = 0, lastT = '';;
    while (tPos < tArr.length) {
        if (sArr[sPos] === tArr[tPos]) {
            lastT = tArr[tPos];
            sPos++;
            tPos++;
        } else if (sArr[sPos] === lastT) {
            sPos++;
        } else {
            return false;
        }
    }

    return true;
}

